function toggleDropdown(){const t=document.querySelector(".open-dropdown"),e=document.querySelector(".dropdown-container");t.addEventListener("click",o=>{e.classList.toggle("show"),t.classList.toggle("active"),o.preventDefault(),o.stopPropagation()}),window.addEventListener("click",o=>{e.contains(o.target)||t.contains(o.target)||(e.classList.remove("show"),t.classList.remove("active"),o.preventDefault(),o.stopPropagation())})}toggleDropdown();