function toggleMobNav(){const e=document.getElementsByClassName("navigation-hamburger")[0],t=document.querySelector(".nav-links");e.addEventListener("click",e=>{t.classList.toggle("mobile"),e.preventDefault(),e.stopPropagation()}),window.addEventListener("click",n=>{t.contains(n.target)||e.contains(n.target)||(t.classList.remove("mobile"),n.preventDefault(),n.stopPropagation())})}toggleMobNav();